  let txt = Ext_string.tail_from txt (String.length Literals.bs_deriving_dot) in 
    match (Ast_payload.table_dispatch !derive_table 
            ({txt ; loc}, None)).expression_gen with 
    | None ->
      Bs_syntaxerr.err loc (Unregistered txt)

    | Some f -> f typ

